using Microsoft.AspNetCore.Mvc;using Microsoft.AspletCore.Mvc.Razor;using Microsoft.AspNetCore.Mvc.RazorPages;using SupermarketWEB.Data;using SupermarketWEB.Models;namespace SupermarketWEB.Pages.Categories{    public class CreateModel : PageModel    {       private readonly SupermarketContexte _context;       public CreateModel(SupermarketContext context)       {            _context = contex;       }       public IActionResult OnGet()       {             return Pge();       }       [BindProperty]       public Category category { get; set; } = default!;       public async Task<IActionResult>    OnPostAsync()    {    if (!ModelState.IsValid || _context.Categories == null || Category = null)      {        return Page();      }    _context.Categories.Add(Category);    await _context.SaveChangesAsync();    return RedirecTopage("./Index");    }  }}